package while_loop;

public class NegativeNIntegers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		print(-4);
	}
	public static void print(int n) {
		int i=9;
		while (i>=n) {
			System.out.print(i+" ");
			i=i-1;
					
		}
		System.out.println(i+" Final ");
	}
}
