package while_loop;

public class OddNNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		print(7);
	}
	public static void print(int n) {
		int i = -9;
		while (i<n) {
			System.out.print(i+" ");
			i++;		
		}
		System.out.print(i+" fff");
	}
}
